  	select prod_plan_no,prod_luno,prod_name,prod_norm,prod_material,prod_length,sum(prod_num) as prod_num_count,sum(prod_rel_weight) as prod_weight_total,prod_level
	from bsl_product_info
	where prod_out_plan = #{planId,jdbcType=VARCHAR}

        and prod_sale_serno = #{planDetailId,jdbcType=VARCHAR} 

	group by prod_plan_no,prod_luno,prod_name,prod_norm,prod_material,prod_length,prod_level

select '' as prod_plan_no, '' as prod_luno, e.enum_value as prod_name, '' as prod_norm, '' as prod_material, '' as prod_length, prod_sumnum as prod_num_count, prod_sumweight as prod_weight_total, '' as prod_level from bsl_sale_info_detail d left join bsl_enum_info e on d.prod_id = e.enum_key and e.enum_english_name = 'wasteType' where d.sale_plan_id = #{planId,jdbcType=VARCHAR}
and d.sale_serno in
#{item}
and d.sale_serno = #{planDetailId,jdbcType=VARCHAR}

  	select prod_plan_no,prod_luno,prod_name,prod_norm,prod_material,prod_length,sum(prod_num) as prod_num_count,sum(prod_print_weight) as prod_weight_total,prod_level
	from bsl_product_info
	where prod_out_plan = #{planId,jdbcType=VARCHAR}

        and prod_sale_serno = #{planDetailId,jdbcType=VARCHAR} 


        and prod_id in

        	#{item}


	group by prod_plan_no,prod_luno,prod_name,prod_norm,prod_material,prod_length,prod_level

select b.bs_customer, b.bs_order_no, b.bs_inputuser, b.bs_flag, e.bs_gettype, p.prod_out_carno, p.prod_ruc, p.prod_out_plan, p.prod_sale_serno, p.prod_out_date, p.prod_name, p.prod_norm, p.prod_dcl_flag, p.prod_material, p.prod_length, '吨' prod_unitz, '支' prod_unit, sum(p.prod_num) sum_prod_num, sum(p.prod_rel_weight) sum_prod_weight, e.cha_weight sum_chaweight, e.ret_weight sum_retweight, b.sale_price, b.sale_price*sum(p.prod_rel_weight) sum_amt, b.sale_price*e.cha_weight sum_cha_amt, b.sale_price*e.ret_weight sum_ret_amt, '人民币' prod_curr, e.print_num from bsl_product_info p left join (select c.bs_customer, c.bs_order_no, c.bs_inputuser, c.bs_chaweight, c.bs_retweight, c.bs_flag, d.sale_serno, d.sale_price from bsl_bs_plan_info c left join bsl_sale_info_detail d on c.bs_id = d.sale_plan_id) b on b.sale_serno = p.prod_sale_serno left join bsl_car_retcha_info e on p.prod_out_carno = e.car_no where p.prod_status in ('4','7') and (#{wxFlag,jdbcType=VARCHAR} = '0' or (#{wxFlag,jdbcType=VARCHAR} = '1' and p.prod_dcl_flag in ('1','2')))
and b.bs_customer = #{bsCustomer,jdbcType=VARCHAR}
and b.bs_order_no = #{bsOrderNo,jdbcType=VARCHAR}
and p.prod_out_plan = #{prodOutPlan,jdbcType=VARCHAR}
and p.prod_sale_serno = #{prodSaleSerno,jdbcType=VARCHAR}
and p.prod_material = #{prodMaterial,jdbcType=VARCHAR}
and p.prod_norm = #{prodNorm,jdbcType=VARCHAR}
and p.prod_dcl_flag = #{prodDclFlag,jdbcType=VARCHAR}
and e.bs_gettype = #{bsGettype,jdbcType=VARCHAR}
and p.prod_length = #{prodLengthF,jdbcType=REAL}
and DATE_FORMAT(p.prod_out_date,'%Y-%m-%d') >= #{dateStart,jdbcType=DATE}
and #{dateEnd,jdbcType=DATE} >= DATE_FORMAT(p.prod_out_date,'%Y-%m-%d')
group by b.bs_customer,b.bs_order_no,b.bs_inputuser,b.bs_flag,e.bs_gettype,p.prod_out_carno,p.prod_ruc,p.prod_out_plan,p.prod_sale_serno, p.prod_out_date,p.prod_name,p.prod_dcl_flag,p.prod_norm,p.prod_material,p.prod_length,b.sale_price order by p.prod_out_carno desc,p.prod_out_plan desc,p.prod_out_date desc

select t.prod_out_carno, t.sum_prod_num, t.sum_prod_weight, t.car_date, t.prod_carno, t1.wx_flag, t1.bs_gettype, t1.ret_weight, t1.cha_weight, p.bs_customer, ifnull(t1.print_num,0) print_num from (select prod_out_carno, prod_out_plan, sum(prod_num) sum_prod_num, sum(prod_rel_weight) sum_prod_weight, substring(prod_out_carno, 2, 14) car_date, substring(prod_out_carno, 16, length(prod_out_carno)-5) prod_carno from bsl_product_info where prod_status in ('4','7') and prod_out_carno is not null
and prod_out_carno like #{prodOutCarno,jdbcType=VARCHAR}
and substring(prod_out_carno, 2, 8) >= #{startDate,jdbcType=VARCHAR}
and #{endDate,jdbcType=VARCHAR} >= substring(prod_out_carno, 2, 8)
group by prod_out_carno,prod_out_plan )t left join bsl_car_retcha_info t1 on t.prod_out_carno = t1.car_no left join bsl_bs_plan_info p on t.prod_out_plan = p.bs_id where 1=1
and ((#{printNum,jdbcType=VARCHAR} = '1' and ifnull(t1.print_num,0) > 0) or (#{printNum,jdbcType=VARCHAR} = '0' and 0 >= ifnull(t1.print_num,0)))
and t1.wx_flag = #{wxFlag,jdbcType=VARCHAR}
order by substring(t.prod_out_carno, 2, 14) desc

select * from( select t1.*, t1.prod_rel_weight - t1.prod_ru_weight prod_fl_weight, concat(left(round(((t1.prod_rel_weight - t1.prod_ru_weight)/t1.prod_rel_weight)*100,2),5),'%') prod_fllv from (select t.prod_out_plan, t.prod_id, t.prod_name, t.prod_type, t.prod_status, t.prod_rel_weight, t.prod_print_weight, t.prod_luno, t.prod_norm, t.prod_material, t.prod_out_date, t.prod_inputuser, t.prod_checkuser, t.crt_date, t.remark, (select ifnull(sum(prod_use_weight),0) from bsl_zjd_use_info p where p.prod_zjd_id = t.prod_id) prod_ru_weight from bsl_product_info t where t.prod_status = '3' and t.prod_out_plan = #{prodOutPlan,jdbcType=VARCHAR}
and t.prod_id like #{prodId,jdbcType=VARCHAR}
) t1 )t2 order by t2.prod_fllv desc,t2.prod_out_date desc

select t.prod_plan_no, t.prod_id, t.prod_name, t.prod_type, t.prod_rel_weight, t.prod_print_weight, t.prod_luno, t.prod_norm, t.prod_material, t.prod_out_date, t.prod_inputuser, t.prod_checkuser, t.crt_date, t.remark, z.prod_zjd_id prod_use_no, z.prod_use_weight, z.prod_use_bl, concat(round(z.prod_use_bl*100,2),'%') prod_use_zb from bsl_product_info t left join bsl_zjd_use_info z on t.prod_id = z.prod_id where t.prod_type = '2' and t.prod_plan_no = #{prodPlanNo,jdbcType=VARCHAR}
and t.prod_id like #{prodId,jdbcType=VARCHAR}
and z.prod_zjd_id like #{prodParentNo,jdbcType=VARCHAR}
order by t.prod_id desc

  		select 
  			* 
  		from bsl_product_info t
  		where
  			t.prod_type = '1'
  			and t.prod_user_type != '1'
  			and t.prod_material = #{prodMaterial,jdbcType=VARCHAR}
  			and t.prod_norm = #{prodNorm,jdbcType=VARCHAR}
  			and (t.prod_status = '1' 
  				or (t.prod_status = '2' and t.prod_out_plan = #{prodOutPlan,jdbcType=VARCHAR}))

		        and t.prod_id like #{prodId,jdbcType=VARCHAR} 


		        and t.prod_plan_no like #{prodPlanNo,jdbcType=VARCHAR} 


		        and t.prod_status = #{prodStatus,jdbcType=VARCHAR} 


		        and t.prod_orirawid like #{prodOrirawid,jdbcType=VARCHAR} 

		    order by prod_status desc,prod_out_date
