mapper.select:
select t.sale_plan_id, t.sale_serno, t.prod_id, t.prod_sumweight, t.prod_jsweight, t.sale_status, t.inputuser, t.crt_date, t.upd_date, t.remark from bsl_sale_info_detail t where t.sale_plan_id in (select bs_id from bsl_bs_plan_info where bs_flag = '11')
and t.sale_plan_id like #{salePlanId,jdbcType=VARCHAR}
and t.sale_serno like #{saleSerno,jdbcType=VARCHAR}
and t.prod_id = #{prodId,jdbcType=VARCHAR}
and DATE_FORMAT(t.crt_date,'%Y-%m-%d') >= #{dateStart,jdbcType=DATE}
and #{dateEnd,jdbcType=DATE} >= DATE_FORMAT(t.crt_date,'%Y-%m-%d')

select t.sale_plan_id, t.sale_serno, t.prod_id, t.sale_weight, t.prod_sumweight, t.sale_status, t.inputuser, t.crt_date, t.upd_date, t.sale_price, p.bs_customer, t.remark from bsl_sale_info_detail t left join bsl_bs_plan_info p on t.sale_plan_id = p.bs_id where ((#{wxFlag,jdbcType=VARCHAR} = '1' and p.bs_flag in ('4','11')) or (#{wxFlag,jdbcType=VARCHAR} = '2' and p.bs_flag = '9'))
and t.sale_plan_id like #{salePlanId,jdbcType=VARCHAR}
and t.sale_serno like #{saleSerno,jdbcType=VARCHAR}
and t.sale_status = #{saleStatus,jdbcType=VARCHAR}
and t.prod_id = #{prodId,jdbcType=VARCHAR}
and DATE_FORMAT(t.crt_date,'%Y-%m-%d') >= #{dateStart,jdbcType=DATE}
and #{dateEnd,jdbcType=DATE} >= DATE_FORMAT(t.crt_date,'%Y-%m-%d')
order by ${orderByClause}
